document.querySelector("[cityform]").addEventListener("click",(e=>{e.preventDefault();const t=document.querySelector("[cityinput]");if(null==t.value||""===t.value)return;let n=t.value.toLowerCase();fetch("https://api.weatherapi.com/v1/current.json?key=88261321c9f046fcade100409230306&q="+n,{mode:"cors"}).then((function(e){return e.json()})).then((function(e){return{cityName:e.location.name,cityTime:e.location.localtime,feelsLike:e.current.condition,temp_c:e.current.temp_c,temp_c_feels:e.current.feelslike_c,humidity:e.current.humidity,uv:e.current.uv,windSpeed:e.current.wind_kph,windDir:e.current.wind_dir}})),t.value=null}));